    function securite($donnees){
        $donnees = trim($donnees);
        $donnees = strip_tags($donnees);
        $donnees = htmlspecialchars($donnees);
        return $donnees;
    }//instruction de la fonctions secutire
    $validationDefault01 = securite($_POST['validationDefault01']);
    $validationDefault02 = securite($_POST['validationDefault02']);
    $colFormLabelSm = securite($_POST['colFormLabelSm']);
